public class 변수설정 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        /*
         *   국어 점수,영어 점수 , 수학 점수 
         *     90      85      80
         */
		int kor=90;
		int eng=85;
		int math=80;
		
		// 점수 출력 
		System.out.println("국어:"+kor);// + : 문자열 결합 , 산술 연산 
		System.out.println("영어:"+eng);
		System.out.println("수학:"+math);
	}

}